import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  RefreshControl,
  Alert,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Ionicons } from '@expo/vector-icons';
import { useAuth } from '../../context/AuthContext';
import { useNotifications } from '../../components/NotificationProvider';
import LoadingSpinner from '../../components/LoadingSpinner';
import { theme, commonStyles } from '../../styles/theme';
import { responsiveWidth, responsiveHeight, responsiveFontSize } from '../../utils/responsive';
import { reportsAPI } from '../../services/api';

const AdminReportsScreen = ({ navigation }) => {
  const { user } = useAuth();
  const { showSuccess, showError } = useNotifications();
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);

  useEffect(() => {
    loadReports();
  }, []);

  const loadReports = async () => {
    try {
      setLoading(true);

      // Load verification reports
      const verificationResponse = await reportsAPI.getVerificationReports();
      const verificationReport = {
        id: 'verifications',
        title: 'Verification Reports',
        type: 'verification',
        description: 'Comprehensive verification statistics and analysis',
        data: verificationResponse.report,
        icon: 'checkmark-circle',
        color: theme.colors.success,
      };

      // Load policy reports
      const policyResponse = await reportsAPI.getPolicyReports();
      const policyReport = {
        id: 'policies',
        title: 'Policy Reports',
        type: 'policy',
        description: 'Policy statistics and breakdown by type',
        data: policyResponse.report,
        icon: 'document-text',
        color: theme.colors.primary,
      };

      // Load audit reports
      const auditResponse = await reportsAPI.getAuditReports();
      const auditReport = {
        id: 'audit',
        title: 'Audit Reports',
        type: 'audit',
        description: 'System activity and user action logs',
        data: auditResponse,
        icon: 'shield-checkmark',
        color: theme.colors.warning,
      };

      setReports([verificationReport, policyReport, auditReport]);
    } catch (error) {
      console.error('Error loading reports:', error);
      showError('Failed to load reports');
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadReports();
    setRefreshing(false);
  };

  const handleExportReport = (report) => {
    Alert.alert(
      'Export Report',
      `Export ${report.title} as CSV?`,
      [
        { text: 'Cancel', style: 'cancel' },
        { 
          text: 'Export', 
          onPress: () => exportReport(report)
        }
      ]
    );
  };

  const exportReport = async (report) => {
    try {
      let response;
      if (report.type === 'verification') {
        response = await reportsAPI.getVerificationReports({ format: 'csv' });
      } else if (report.type === 'policy') {
        response = await reportsAPI.getPolicyReports({ format: 'csv' });
      } else {
        showError('Export not available for this report type');
        return;
      }

      showSuccess('Report exported successfully');
    } catch (error) {
      console.error('Error exporting report:', error);
      showError('Failed to export report');
    }
  };

  const ReportCard = ({ report }) => (
    <View style={styles.reportCard}>
      <View style={styles.reportHeader}>
        <View style={[styles.reportIcon, { backgroundColor: report.color }]}>
          <Ionicons name={report.icon} size={responsiveFontSize(24)} color={theme.colors.white} />
        </View>
        <View style={styles.reportInfo}>
          <Text style={styles.reportTitle}>{report.title}</Text>
          <Text style={styles.reportDescription}>{report.description}</Text>
        </View>
      </View>

      {report.data && (
        <View style={styles.reportStats}>
          {report.type === 'verification' && (
            <>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.statistics?.total_verifications || 0}</Text>
                <Text style={styles.statLabel}>Total Verifications</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.statistics?.valid_count || 0}</Text>
                <Text style={styles.statLabel}>Valid</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.statistics?.fake_count || 0}</Text>
                <Text style={styles.statLabel}>Fake</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.statistics?.fake_detection_rate || 0}%</Text>
                <Text style={styles.statLabel}>Fake Rate</Text>
              </View>
            </>
          )}

          {report.type === 'policy' && (
            <>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.statistics?.total_policies || 0}</Text>
                <Text style={styles.statLabel}>Total Policies</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.statistics?.active_count || 0}</Text>
                <Text style={styles.statLabel}>Active</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.statistics?.expired_count || 0}</Text>
                <Text style={styles.statLabel}>Expired</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.statistics?.cancelled_count || 0}</Text>
                <Text style={styles.statLabel}>Cancelled</Text>
              </View>
            </>
          )}

          {report.type === 'audit' && (
            <>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.pagination?.total || 0}</Text>
                <Text style={styles.statLabel}>Total Logs</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statValue}>{report.data.audit_logs?.length || 0}</Text>
                <Text style={styles.statLabel}>Recent Logs</Text>
              </View>
            </>
          )}
        </View>
      )}

      <View style={styles.reportActions}>
        <TouchableOpacity 
          style={[styles.actionButton, styles.viewButton]}
          onPress={() => navigation.navigate('ReportDetails', { report })}
        >
          <Ionicons name="eye-outline" size={responsiveFontSize(16)} color={theme.colors.primary} />
          <Text style={styles.actionButtonText}>View Details</Text>
        </TouchableOpacity>

        <TouchableOpacity 
          style={[styles.actionButton, styles.exportButton]}
          onPress={() => handleExportReport(report)}
        >
          <Ionicons name="download-outline" size={responsiveFontSize(16)} color={theme.colors.success} />
          <Text style={styles.actionButtonText}>Export</Text>
        </TouchableOpacity>
      </View>
    </View>
  );

  if (loading) {
    return (
      <SafeAreaView style={styles.container}>
        <LoadingSpinner message="Loading reports..." />
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView 
        style={styles.scrollView}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        {/* Header */}
        <View style={styles.header}>
          <View style={styles.headerLeft}>
            <Text style={styles.headerTitle}>Reports & Analytics</Text>
            <Text style={styles.headerSubtitle}>
              Comprehensive system reports and statistics
            </Text>
          </View>
          <TouchableOpacity 
            style={styles.refreshButton}
            onPress={onRefresh}
          >
            <Ionicons name="refresh" size={responsiveFontSize(24)} color={theme.colors.primary} />
          </TouchableOpacity>
        </View>

        {/* Reports List */}
        <View style={styles.reportsContainer}>
          <Text style={styles.sectionTitle}>
            Available Reports ({reports.length})
          </Text>

          {reports.length === 0 ? (
            <View style={styles.emptyState}>
              <Ionicons name="document-outline" size={responsiveFontSize(64)} color={theme.colors.textMuted} />
              <Text style={styles.emptyTitle}>No reports available</Text>
              <Text style={styles.emptySubtitle}>
                Reports will appear here once data is available
              </Text>
            </View>
          ) : (
            reports.map((report) => (
              <ReportCard key={report.id} report={report} />
            ))
          )}
        </View>

        {/* Quick Stats */}
        <View style={styles.quickStatsContainer}>
          <Text style={styles.sectionTitle}>Quick Statistics</Text>
          <View style={styles.quickStatsGrid}>
            <View style={styles.quickStatCard}>
              <Ionicons name="checkmark-circle" size={responsiveFontSize(32)} color={theme.colors.success} />
              <Text style={styles.quickStatValue}>
                {reports.find(r => r.type === 'verification')?.data?.statistics?.total_verifications || 0}
              </Text>
              <Text style={styles.quickStatLabel}>Total Verifications</Text>
            </View>

            <View style={styles.quickStatCard}>
              <Ionicons name="document-text" size={responsiveFontSize(32)} color={theme.colors.primary} />
              <Text style={styles.quickStatValue}>
                {reports.find(r => r.type === 'policy')?.data?.statistics?.total_policies || 0}
              </Text>
              <Text style={styles.quickStatLabel}>Total Policies</Text>
            </View>

            <View style={styles.quickStatCard}>
              <Ionicons name="warning" size={responsiveFontSize(32)} color={theme.colors.danger} />
              <Text style={styles.quickStatValue}>
                {reports.find(r => r.type === 'verification')?.data?.statistics?.fake_count || 0}
              </Text>
              <Text style={styles.quickStatLabel}>Fake Detections</Text>
            </View>

            <View style={styles.quickStatCard}>
              <Ionicons name="shield-checkmark" size={responsiveFontSize(32)} color={theme.colors.warning} />
              <Text style={styles.quickStatValue}>
                {reports.find(r => r.type === 'audit')?.data?.pagination?.total || 0}
              </Text>
              <Text style={styles.quickStatLabel}>Audit Logs</Text>
            </View>
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

