              onPress={async () => {
                Alert.alert(
                  'Logout',
                  'Are you sure you want to logout?',
                  [
                    { text: 'Cancel', style: 'cancel' },
                    { 
                      text: 'Logout', 
                      style: 'destructive',
                      onPress: async () => {
                        try {
                          const result = await logout();
                          if (result.success) {
                            showSuccess('Logged out successfully');
                            // Force navigation reset to ensure proper redirect
                            setTimeout(() => {
                              navigation.reset({
                                index: 0,
                                routes: [{ name: 'Login' }],
                              });
                            }, 1000);
                          } else {
                            showError('Failed to logout. Please try again.');
                          }
                        } catch (error) {
                          console.error('Logout error:', error);
                          showError('Failed to logout. Please try again.');
                        }
                      }
                    }
                  ]
                );
              }}
            >
              <Ionicons name="log-out-outline" size={responsiveFontSize(20)} color={theme.colors.danger} />
            </TouchableOpacity>
            <TouchableOpacity 
              style={styles.profileButton}
              onPress={() => navigation.navigate('Profile')}
            >
              <Ionicons name="person-circle" size={responsiveFontSize(40)} color={theme.colors.primary} />
            </TouchableOpacity>
          </View>
        </View>

